import XCTest

class UtilityTest: XCTestCase {

    var u:Utility!
    
    override func setUp() {
        // Put setup code here. This method is called before the invocation of each test method in the class.
        u = Utility()
    }

    override func tearDown() {
        // Put teardown code here. This method is called after the invocation of each test method in the class.
        u = nil
    }

    func testExample() {
        // This is an example of a functional test case.
        // Use XCTAssert and related functions to verify your tests produce the correct results.
        XCTAssertNotNil(u)
    }
    
    func testValidateIpv6() {
        var str = "2001:0db8:85a3:0000:0000:8a2e:0370:7334"
        var r = u.validateIpv6(str)
        XCTAssertTrue(r)
        str = "2001:0db8:85a3::0000:8a2e:0370:7334"
        r = u.validateIpv6(str)
        XCTAssertFalse(r)
        str = "02001:0db8:85a3::0000:8a2e:0370:7334"
        r = u.validateIpv6(str)
        XCTAssertFalse(r)
        str = "2001:0db8:85a3:0000:0000:8a2e:0370:733w"
        r = u.validateIpv6(str)
        XCTAssertFalse(r)
        str = "1e1.4.5.6"
        str = "..."
        str = "2001:0db8:85a3:0:0:8A2E:0370:7334"
        str = "2001:0db8:85a3:0:0:8A2E:0370:7334:"
        str = "2001:0db8:85a3:00000:0:8A2E:0370:7334"
        r = u.validateIpv6(str)
        
        XCTAssertFalse(r)
    }
    func testValidateIpv4() {
        var str = "172.16.254.1"
        var r = u.validateIpv4(str)
        XCTAssertTrue(r)
        str = "1e1.4.5.6"
        str = "..."
        str = "1.1.1.1."
        str = "01.01.01.01"
        str = "192.0.0.1"
        r = u.validateIpv4(str)
        XCTAssertTrue(r)
    }

    func testPerformanceExample() {
        // This is an example of a performance test case.
        self.measure {
            // Put the code you want to measure the time of here.
        }
    }

}
